mapper.insert:
INSERT INTO apartment_info
(
      dealamount, buildyear, dealyear, dealmonth, dealday
    , dong, apartmentname, areaforexclusiveuse, jibun, `floor`
    , locationcode, excd
)
SELECT
      #{dealamount}, #{buildyear}, #{dealyear}, #{dealmonth}, #{dealday}
    , #{dong}, #{apartmentname}, #{areaforexclusiveuse}, #{jibun}, #{floor}
    , `code`, #{excd}
FROM location_code
WHERE excd = #{excd}
